            $(document).on('click', '#pendingButton', function (e) {
                e.preventDefault();
                $.fn.dataTable.ext.search.push(
                    function (settings, data, dataIndex, row) {
                        return $(table.row(dataIndex).node()).attr('data-status') == "Pending";
                    }
                );
                table.draw();
                
                $('.btn').each(function () {
                    $(this).removeClass('active')
                });
                //then the click button add class active 
                if ($(this).hasClass('active')) {
                    $(this).removeClass('active')
                } else {
                    $(this).addClass('active')
                }
            });

