        $(document).on('click', '.detail-btn', function (e) {
            e.preventDefault();
                   
		            const id = $(this).attr('data-id');
                    $.ajax({
                        url: 'appointment-details/' + id,
                        type: 'GET',
                        data: {
                            "id": id
                        },
                        success: function (data) {
                            console.log(data);
                            //$('#patient-fullname').html(data.lastname+", "+data.firstname+" "+data.middlename);
                            if (data.patient.middlename == null) {
                                $('#appointment-name').html(data.patient.firstname + " " + data.patient.lastname);
                            } else if (data.patient.middlename != null) {
                                $('#appointment-name').html(data.patient.firstname + " " + data.patient.middlename + " " + data.patient.lastname);
                            }

                            var ppt = data.patient.patient_category;
                            //alert(ppt);
                            if (ppt == "Student") {
                                $('#appointment-patient-category').html('<span class="badge badge-success">' + data.patient.patient_category + '</span>');
                            }else if(ppt == "Administrator") {
                                $('#appointment-patient-category').html('<span class="badge badge-primary">' + data.patient.patient_category + '</span>');
                            }else if(ppt == "Faculty") {
                                $('#appointment-patient-category').html('<span class="badge badge-danger">' + data.patient.patient_category + '</span>');
                            }else if(ppt == "Dependent") {
                                $('#appointment-patient-category').html('<span class="badge badge-secondary">' + data.patient.patient_category + '</span>');
                            }
                            //$('#appointment-patient-category').html(data.patient.patient_category);
                            $('#appointment-service').html(data.service.service);
                            $('#appointment-description').html(data.description);
                            var aptDate = new Date(data.date);
                           // var t = aptDate.toDateString();
                            //alert(t);
                            var t = moment(aptDate).format('MMMM DD, YYYY (dddd)')
                           
                            $('#appointment-date').html(t);
                            $('#appointment-time').html(data.time);
                            if (data.status == "Pending") {
                                $('#appointment-status').html('<span class="badge badge-warning">' + data.status + '</span>');
                                $('#appointment-progress').html('<div class="progress progress-xs progress-striped active" style="height: 7px;"><div class="progress-bar bg-warning" style="width: 50%"></div></div><span class="badge badge-warning" style="margin-left:40%;">50%</span>');
                            } else if (data.status == "Scheduled") {
                                $('#appointment-status').html('<span class="badge badge-success">' + data.status + '</span>');
                                $('#appointment-progress').html('<div class="progress progress-xs progress-striped active" style="height: 7px;"><div class="progress-bar bg-success" style="width: 75%"></div></div><span class="badge badge-success" style="margin-left:40%;">75%</span>');
                            }else if (data.status == "Closed") {
                                $('#appointment-status').html('<span class="badge badge-danger">' + data.status + '</span>');
                                $('#appointment-progress').html('<div class="progress progress-xs progress-striped active" style="height: 7px;"><div class="progress-bar bg-danger" style="width: 100%"></div></div><span class="badge badge-danger" style="margin-left:40%;">100%</span>');
                            }else if (data.status == "Reschedule") {
                                $('#appointment-status').html('<span class="badge badge-info">' + data.status + '</span>');
                                $('#appointment-progress').html('<div class="progress progress-xs progress-striped active" style="height: 7px;"><div class="progress-bar bg-info" style="width: 25%"></div></div><span class="badge badge-info" style="margin-left:40%;">25%</span>');
